def create_bunny(egg_texture):
    bunny = json.loads(ORIGINAL_BUNNY_CROWN)

    bunny["display"]["gui"] = {
        "rotation": [18, 154, 0],
        "translation": [0, -10, 0],
        "scale": [2, 2, 2],
    }

    del bunny["textures"]["0"]
    del bunny["textures"]["2"]
    del bunny["textures"]["3"]

    bunny["textures"]["5"] = egg_texture

    bunny["elements"] = [
        element
        for element in bunny["elements"]
        if element["faces"]["north"]["texture"] in ("#4", "#5")
    ]

    return bunny
